import {useEffect, useState} from "react";
import axios from 'axios';
import Footer from "./Footer";
import SpotifyWebApi from "spotify-web-api-node"
import { Container, Form } from "react-bootstrap"
import Player from "./Player";
import TrackSearchResult from "./TrackSearchResult";


export default spotifyApi = new SpotifyWebApi({
  clientId: "8b945ef10ea24755b83ac50cede405a0",
})

function Login() {

    const CLIENT_ID = "5609644436ef41f7b05c81029bc84fbf"
    const REDIRECT_URI = "http://localhost:3000/"
    const AUTH_ENDPOINT = "https://accounts.spotify.com/authorize"
    const SCOPE = "streaming%20user-read-email%20user-read-private%20user-library-read%20user-library-modify%20user-read-playback-state%20user-modify-playback-state"
    const RESPONSE_TYPE = "token"

    const [token, setToken] = useState("")
    const [searchKey, setSearchKey] = useState("")
    const [artists, setArtists] = useState([])
    const [search, setSearch] = useState("")
    const [searchResults, setSearchResults] = useState([])
    const [playingTrack, setPlayingTrack] = useState()
    const [lyrics, setLyrics] = useState("")
  

    // const getToken = () => {
    //     let urlParams = new URLSearchParams(window.location.hash.replace("#","?"));
    //     let token = urlParams.get('access_token');
    // }

 //token purpose/////////////////////////////////////////
    useEffect(() => {
        const hash = window.location.hash
        let token = window.localStorage.getItem("token")
        if (!token && hash) {
            token = hash.substring(1).split("&").find(elem => elem.startsWith("access_token")).split("=")[1]
            window.location.hash = ""
            window.localStorage.setItem("token", token)
        }
        console.log(token)
        setToken(token)
    }, [])
  ////////////////////////////token purpose ////////////////////////////////////////////////////////////////////
    
  ///////////////////////log out /////////////////
  const logout = () => {
      setToken("")
      window.localStorage.removeItem("token")
    }
    //////////////log out //////////////////

    useEffect(() => {
      if (!token) return
      spotifyApi.setAccessToken(token)
    }, [token])
  
    useEffect(() => {
        if (!search) return setSearchResults([])
        if (!token) return
    
        let cancel = false
        spotifyApi.searchTracks(search).then(res => {
            if (cancel) return
            setSearchResults(
                res.body.tracks.items.map(track => {
                    const smallestAlbumImage = track.album.images.reduce(
                        (smallest, image) => {
                            if (image.height < smallest.height) return image
                            return smallest
                        },
                        track.album.images[0]
                    )
    
                    return {
                        artist: track.artists[0].name,
                        title: track.name,
                        uri: track.uri,
                        albumUrl: smallestAlbumImage.url,
                    }
                })
            )
        })
    })


    const searchArtists = async (e) => {
        e.preventDefault() 
        
        const {data} = await axios.get("https://api.spotify.com/v1/search", {
            headers: {
                Authorization: `Bearer ${token}`
            },
            params: {
                q: searchKey,
                type: "artist",
                limit: 50
            }
        })
        console.log(data);
        setArtists(data.artists.items)
    }

    const renderArtists = () => {
        return artists.map(artist => (
            <div className="flex flex-col gap-2 p-5 transition ease-in-out delay-10 hover:bg-red-900 hover:translate-y-5" key={artist.id}>
                <div className='flex p-4'>{artist.images.length ? <img width={90} height={90} className='flex rounded-full'
                    src={artist.images[0].url} alt="" /> : <div>No </div>}
                </div>
                    <div className='ml-4'>{artist.name}</div>
                    <div className='ml-4 text-slate-400'>Artists</div>
                    <div className='ml-4 text-slate-400'>{artist.genres[0]}</div>
            </div>
        ))
    }

    return (
      <div className="App text-white bg-black">
            <header className="App-header flex gap-4 justify-center p-6">
                <div>
                    {token ?  <form onSubmit={searchArtists}>
                        <input type="text" className="text-black" placeholder="artists" onChange={e => setSearchKey(e.target.value)} />
                        <button type={"submit"}>Search</button>
                    </form>
                        : <div>login</div>}
                {!token ?
                    <a href={`${AUTH_ENDPOINT}?client_id=${CLIENT_ID}&redirect_uri=${REDIRECT_URI}&response_type=${RESPONSE_TYPE}&scope=${SCOPE}`}>Login to Spotify</a>
                    : <button onClick={logout} className="flex">LOGOUT</button>}
                </div>
                <br/>
            </header>

            <div className="grid grid-cols-6 m-4 p-4 text-white">
                {renderArtists()}
        </div>
        <div>
          <Footer/>
            </div>

        <Container className="d-flex flex-column py-2" style={{ height: "100vh" }}>
      <Form.Control
        type="search"
        placeholder="Search Songs/Artists"
        value={search}
        onChange={e => setSearch(e.target.value)}
      />
      <div className="flex-grow-1 my-2" style={{ overflowY: "auto" }}>
        {searchResults.map(track => (
          <TrackSearchResult
            track={track}
            key={track.uri}
            chooseTrack={chooseTrack}
          />
        ))}
        {searchResults.length === 0 && (
          <div className="text-center" style={{ whiteSpace: "pre" }}>
            {lyrics}
          </div>
        )}
      </div>
                <div>
        <Player accessToken={token} trackUri={playingTrack?.uri} />
      </div>
    </Container>

        </div> 

        
    );
}
