import {useEffect, useState} from "react";
import axios from 'axios';
import Footer from "./Footer";
function Login() {

    const CLIENT_ID = "3fe91bd9d5d0463192d383d6cee121e8"
    const REDIRECT_URI = "http://localhost:3000/Search"
    const AUTH_ENDPOINT = "https://accounts.spotify.com/authorize"
    const SCOPE = "streaming%20user-read-email%20user-read-private%20user-library-read%20user-library-modify%20user-read-playback-state%20user-modify-playback-state"
    const RESPONSE_TYPE = "token"

    const [token, setToken] = useState("")
    const [searchKey, setSearchKey] = useState("")
    const [artists, setArtists] = useState([])
  
    useEffect(() => {
        const hash = window.location.hash
        let token = window.localStorage.getItem("token")
        // getToken()
        if (!token && hash) {
            token = hash.substring(1).split("&").find(elem => elem.startsWith("access_token")).split("=")[1]
            window.location.hash = ""
            window.localStorage.setItem("token", token)
        }

        console.log(token)
        setToken(token)
    }, [])

    const logout = () => {
        setToken("")
        window.localStorage.removeItem("token")
    }

    const searchArtists = async (e) => {
        e.preventDefault() 
        const {data} = await axios.get("https://api.spotify.com/v1/search", {
            headers: {
                Authorization: `Bearer ${token}`
            },
            params: {
                q: searchKey,
                type: "artist",
                type: "track",
                limit: 50
            }
        })
        console.log(data);
        setArtists(data.artists.items)
    }

    // const renderArtists = () => {
    //     return artists.map(artist => (
    //         <div className="flex flex-col gap-2 p-5 transition ease-in-out delay-10 hover:bg-red-900 hover:translate-y-5" key={artist.id}>
    //             <div className='flex p-4'>{artist.images.length ? <img width={90} height={90} className='flex rounded-full'
    //                 src={artist.images[0].url} alt="" /> :<div>No </div>}
    //             </div>
    //                 <div className='ml-4'>{artist.name}</div>
    //                 <div className='ml-4 text-slate-400'>Artists</div>
    //         </div>
    //     ))
    // }

    return (
            <div className="App text-white bg-black">
            <header className="App-header flex gap-4 justify-center p-6">
                {!token ?
                    <a className="" href={`${AUTH_ENDPOINT}?client_id=${CLIENT_ID}&redirect_uri=${REDIRECT_URI}&response_type=${RESPONSE_TYPE}&scope=${SCOPE}`}>Login to Spotify</a>
                    : <button onClick={logout} className="flex">LOGOUT</button>}
                <div>
                    {token ?  <form onSubmit={searchArtists}>
                        <input type="text" className="text-black" onChange={e => setSearchKey(e.target.value)} />
                        <button type={"submit"}>Search</button>
                    </form> : <div>login</div>}
                </div>

                <br/>
    
            </header>

            <div className="grid grid-cols-6 m-4 p-4 text-white">
                {/* {renderArtists()} */}
                </div>

        <Footer/>
        </div> 
    );
}

export default Login;
